import * as React from "react"
import { Frame, addPropertyControls, ControlType } from "framer"

// Open Preview (CMD + P)
// API Reference: https://www.framer.com/api

export function DefaultSpinner(props) {
    return (
        <Frame
            animate={props.animate}
            y={props.y}
            height={50}
            width={50}
            center={"x"}
            image={props.image}
        />
    )
}

addPropertyControls(DefaultSpinner, {
    image: {
        type: ControlType.Image,
        title: "Spinner Image",
    },
})
